import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import Dashboard from "@material-ui/icons/Dashboard";
import Schedule from "@material-ui/icons/Schedule";
import List from "@material-ui/icons/List";

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import NavPills from "components/NavPills/NavPills.js";

import styles from "assets/jss/material-kit-react/views/componentsSections/pillsStyle.js";
import { ChildCare } from "@material-ui/icons";

const useStyles = makeStyles(styles);

export default function SectionPills() {
  const classes = useStyles();
  return (
    <div className={classes.section}>
      <div className={classes.container}>
        <div id="navigation-pills" justify="center">
          <div className={classes.title}>
            <h1>We Have Learning  Materials for Every Level</h1>
          </div>
          <GridContainer >
            <div justify="center">
              <NavPills
                color="primary"
                tabs={[
                  {
                    tabButton: "For Beginners",
                    tabIcon: Dashboard,
                    tabContent: (
                      <span>
                        <p>
                        Basic conversation, grammar, vocabulary practice. Lessons for those just starting to learn!
                        </p>
                        <br />
                      </span>
                    )
                  },
                  {
                    tabButton: "For Kids",
                    tabIcon: ChildCare,
                    tabContent: (
                      <span>
                        <p>
                        Alphabet, Letâ€™s Go ... Popular English materials for kids
                        </p>
                        <br />
                      </span>
                    )
                  },
                  {
                    tabButton: "Business",
                    tabIcon: List,
                    tabContent: (
                      <span>
                        <p>
                        Practice useful English for office workers
                        </p>
                        <br />
                      </span>
                    )
                  },
                  {
                    tabButton: "Speaking Test",
                    tabIcon: Dashboard,
                    tabContent: (
                      <span>
                        <p>
                        Familiarize yourself with TOEIC Speaking, TOEFL Speaking test
                        </p>
                        <br />
                      </span>
                    )
                  },
                  {
                    tabButton: "Travel & Culture",
                    tabIcon: Dashboard,
                    tabContent: (
                      <span>
                        <p>
                        Learn phrases you will need when traveling abroad
                        </p>
                      </span>
                    )
                  },
                  {
                    tabButton: "Discussion",
                    tabIcon: Dashboard,
                    tabContent: (
                      <span>
                        <p>
                        High quality English lessons based on news articles - updated daily!
                        </p>
                        <br />
                      </span>
                    )
                  }
                ]}
              />
            </div>
          </GridContainer>
        </div>
      </div>
    </div>
  );
}
